import React, { useState } from "react";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";
import Video from "./Video";

class VideoModal extends React.Component {
  VideoModalFunction = () => {
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
      <div class="container">
        <Button bg="primary" onClick={handleShow}>
          {this.props.buttontext}
        </Button>
        <Modal show={show} onHide={handleClose}>
          <Modal.Header>{this.props.headerText}</Modal.Header>
          <Modal.Body>
            <Video videoUrl={this.props.videoUrl} title={this.props.title} />
          </Modal.Body>
          <Modal.Footer>
            <Button bg="primary" onClick={handleClose}>
              Close Video
            </Button>
          </Modal.Footer>
        </Modal>
      </div>
    );
  };

  render(VideoModalFunction) {}
}

export default VideoModal;
